    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handlin 
        //String forma=(String) form.getSelectedItem();
        String material=(String)mat.getSelectedItem();
        String panel=(String)pan.getSelectedItem();
        /*System.out.println(panel);
        System.out.println(forma);
        System.out.println(material);*/
        if(material.equals("Vinyl")&& panel.equals("0")){
            text.setText("Pelota playa");
            img.setIcon(new ImageIcon(getClass().getResource("playa.jpg")));
         }
        
      else if(material.equals("Piel")&& panel.equals("Mas de 4")){
        text.setText("Balon de soccer");
        img.setIcon(new ImageIcon(getClass().getResource("soccer.jpg")));
       
       }
      else if(material.equals("Cuero")&& panel.equals("Mas de 4")){
          text.setText("Balon de voley");
        img.setIcon(new ImageIcon(getClass().getResource("voly.jpg")));
       }
       
       else{
        text.setText("NO HAY OBJETO");
        img.setIcon(new ImageIcon(getClass().getResource("error.png")));
       }
        
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

